-- CREATE USERS TABLE
CREATE TABLE `railway`.`users` ( `id` INT NOT NULL AUTO_INCREMENT , `name` VARCHAR(100) NOT NULL , `phone` VARCHAR(15) NOT NULL , `password` VARCHAR(225) NOT NULL , `gender` ENUM('male','female') NULL DEFAULT NULL , `city` VARCHAR(100) NULL DEFAULT NULL , `role` ENUM('user','admin','super') NOT NULL DEFAULT 'user' , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;

-- CREATE TASKS TABLE
CREATE TABLE `railway`.`tasks` ( `id` INT NOT NULL AUTO_INCREMENT , `user_id` INT NOT NULL , `name` VARCHAR(100) NOT NULL , `description` VARCHAR(225) NOT NULL DEFAULT 'Tidak ada deskripsi' , `status` ENUM('Todo','Doing','Done') NOT NULL DEFAULT 'Todo' , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;